def swc_creation (list_id, data,structure_name) :
    '''
    For a given list of neuron ids (and their associated database) it saves their morphology in a swc file

    Parameters
    ----------
    liste_id : List
        list of neuron ids we want the morphology savec in a swc file

    data : list of dictionary or None
        the neuromorpho database if we already have it

    structure_name : str
    name of the structure : 'primary visual'

    Returns
    -------
    The swc file is saved.

    '''

    for i in list_id :
        if data == None :
            data = nmo.search({"species": "mouse", "brain_region": structure_name})
        cell_info=data[i]
        if "." in cell_info['neuron_name'] :
            z=cell_info['neuron_name'].replace(".","_")
            name=z+'.swc'
        else :
            name=cell_info['neuron_name']+'.swc'
        download=nmo.download_swc(data[i])
        save_swc(name,download)

